
    $(document).ready(function() {
        $.validator.setDefaults({
            errorClass: 'help-block',
            highlight: function(element) {
                $(element)
                    .closest('.form-grouping')
                    .addClass('has-error');
            },
            unhighlight: function(element) {
                $(element)
                    .closest('.form-grouping')
                    .removeClass('has-error');
            }
        });
    });
